import { Component, ElementRef, ViewChild } from '@angular/core';
import { FormArray, FormBuilder, FormControl, FormGroup, FormsModule, ReactiveFormsModule, Validators } from '@angular/forms';
import { AppModule } from 'src/app/app.module';


@Component({
  selector: 'form-task-c',
  templateUrl: './form-task.component.html',
  styleUrls: ['./form-task.component.css'],
  imports:[ ReactiveFormsModule, AppModule],
  standalone:true,
})
export class FormTaskComponent {

  taskForm: FormGroup;
  userForm: FormGroup;
  constructor(
    private fb: FormBuilder
  ){
    // se crea el formulario de las tareas
    this.taskForm = this.fb.group({
      taskName: ["", Validators.required],
      date: ["", Validators.required],
      users: this.fb.array([]),
    });

    // se crea el formulario de los usuarios
    this.userForm = this.fb.group({
      username: ["", Validators.required],
      age: ["", Validators.required],
      skills: this.fb.array([]),
    });

  }

  get users(){
    return this.taskForm.controls["users"] as FormArray; 
  }

  addUser(){ 
    const userForm = this.fb.group({
      username: ["", Validators.required],
      age: ["", Validators.required],
      skills: this.fb.array([]),
    });
    this.users.push(userForm);
  }

}
