import { Component, ElementRef, ViewChild } from '@angular/core';
import { Form, FormArray, FormBuilder, FormControl, FormGroup, FormsModule, ReactiveFormsModule, Validators } from '@angular/forms';


@Component({
  selector: 'form-task-c',
  templateUrl: './form-task.component.html',
  styleUrls: ['./form-task.component.css'],
})
export class FormTaskComponent {

  taskForm: FormGroup;
  userForm: FormGroup;
  @ViewChild('skill') skillInp!: ElementRef;

  constructor(
    private fb: FormBuilder
  ) {
    // se crea el formulario de las tareas
    this.taskForm = this.fb.group({
      taskName: ["", Validators.required],
      date: ["", Validators.required],
      users: this.fb.array([]),
    });

    // se crea el formulario de los usuarios
    this.userForm = this.fb.group({
      username: ["", Validators.required],
      age: ["", Validators.required],
      skills: this.fb.array([
        this.fb.control("Java"),
        this.fb.control("Angular"),
        this.fb.control("Mysql"),
        this.fb.control("AWS"),
      ]),
    });

  }

  get users() {
    return this.taskForm.controls["users"] as FormArray;
  }

  get skills() {
    return this.userForm.controls["skills"] as FormArray;
  }

  addUser() {
    const userForm = this.fb.group({
      username: ["", Validators.required],
      age: ["", Validators.required],
      skills: this.fb.array([
        this.fb.control("java"),
      ]),
    });
    this.users.push(userForm);
  }


  submitUser() {
    console.log("this.userForm.value>");
    console.log(this.userForm.valid);
    console.log(this.userForm.value);
    console.log(this.userForm.controls);
  }

  get fU(){
    return this.userForm.controls["skills"] as FormArray
  }

  // agrega una habilidad al usuario 
  addSkill() {
    const skill = this.skillInp.nativeElement.value;
    if (!skill) {
      return
    }
    const controlSkill = this.fb.control(skill);
    this.skills.push(controlSkill);
    this.skillInp.nativeElement.value = "";
  }

  // Elimina una habilidad
  deleteSkill(index:number){
    const skills = this.skills;
    console.log("skills->")
    console.log(skills)
  }

}
